import useStats from '../utils/useStats';
import styled from 'styled-components';

const StatGrid = styled.div`
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    grid-gap: 1rem;
`;

const StatBlock = styled.div`
    align-items: center;
    background: #f2f2f2;
    border-radius: 2rem;
    display: grid;
    font-size: 2rem;
    justify-items: center;
    padding: 2rem;
    text-align: center;
`;

export default function Stats({ url }) {
    const { stats, loading, error } = useStats(url);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error...</p>;

    return (
        <StatGrid>
            <StatBlock>
                <h3>Confirmed:</h3>
                <span>
                    {stats.confirmed.value}
                </span>
            </StatBlock>
            <StatBlock>
                <h3>Deaths:</h3>
                <span>
                    {stats.deaths.value}
                </span>
            </StatBlock>
            <StatBlock>
                <h3>Recovered:</h3>
                <span>
                    {stats.recovered.value}
                </span>
            </StatBlock>
            <StatBlock>
                <h3>Last Update:</h3>
                <span>
                    {stats.lastUpdate}
                </span>
            </StatBlock>

        </StatGrid>
    )
}
